import React, { useState } from 'react';
import { Box, TextField, Button, Typography, Paper } from '@mui/material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';

interface DateSelectorProps {
  onDateSelected: (date: Date) => void;
}

const DateSelector: React.FC<DateSelectorProps> = ({ onDateSelected }) => {
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [dateError, setDateError] = useState<string | null>(null);

  // Enforce minimum date (today)
  const minDate = new Date();
  minDate.setHours(0, 0, 0, 0);

  const handleDateChange = (date: Date | null) => {
    setSelectedDate(date);
    setDateError(null);
  };

  const handleSubmit = () => {
    if (!selectedDate) {
      setDateError('Please select a date');
      return;
    }

    // Check if date is valid and not in the past
    if (selectedDate < minDate) {
      setDateError('Please select a date in the future');
      return;
    }

    onDateSelected(selectedDate);
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Paper
        elevation={1}
        sx={{
          p: 2,
          borderRadius: 2,
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
          animation: 'fadeIn 0.3s ease-out',
          '@keyframes fadeIn': {
            '0%': { opacity: 0, transform: 'translateY(10px)' },
            '100%': { opacity: 1, transform: 'translateY(0)' }
          }
        }}
      >
        <Typography 
          variant="subtitle1" 
          sx={{ mb: 1, fontWeight: 500, color: '#333' }}
        >
          Select a date for your study room
        </Typography>
        
        <Box sx={{ mb: 2 }}>
          <DatePicker
            label="Date (MM/DD/YYYY)"
            value={selectedDate}
            onChange={handleDateChange}
            minDate={minDate}
            slotProps={{
              textField: {
                fullWidth: true,
                variant: 'outlined',
                error: !!dateError,
                helperText: dateError
              }
            }}
          />
        </Box>
        
        <Button
          variant="contained"
          onClick={handleSubmit}
          sx={{
            bgcolor: '#FF6B81',
            '&:hover': { bgcolor: '#FF4757' },
            borderRadius: '20px',
            px: 3
          }}
        >
          Continue
        </Button>
      </Paper>
    </LocalizationProvider>
  );
};

export default DateSelector;
