import React, { useState } from 'react';
import { Box, Paper, Typography, Grid, Button } from '@mui/material';
import { 
  LocalizationProvider, 
  TimePicker,
  DigitalClock
} from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import dayjs, { Dayjs } from 'dayjs';

interface TimeSelectorProps {
  date: string;
  onTimeSelect: (startTime: string, endTime: string) => void;
  availableTimes?: { start: string; end: string }[];
  availabilityStatus?: 'available' | 'limited' | 'unavailable';
}

const TimeSelectorMUI: React.FC<TimeSelectorProps> = ({ 
  date, 
  onTimeSelect, 
  availableTimes,
  availabilityStatus = 'available' // Default to fully available
}) => {
  const [selectedTime, setSelectedTime] = useState<Dayjs | null>(null);
  
  // For display and conversion purposes
  const formatTime = (timeStr: string) => {
    const [hours, minutes] = timeStr.split(':').map(Number);
    const period = hours >= 12 ? 'PM' : 'AM';
    const formattedHours = hours % 12 || 12; // Convert 0 to 12 for 12 AM
    return `${formattedHours}:${minutes.toString().padStart(2, '0')} ${period}`;
  };

  // Format the date for display
  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      month: 'long',
      day: 'numeric'
    });
  };
  
  // Get availability status text and color
  const getAvailabilityInfo = () => {
    switch (availabilityStatus) {
      case 'available':
        return { text: 'All time slots available', color: '#52C41A' };
      case 'limited':
        return { text: 'Limited availability', color: '#FFA940' };
      case 'unavailable':
        return { text: 'No availability on this date', color: '#FF4D4F' };
      default:
        return { text: '', color: 'inherit' };
    }
  };
  
  const availabilityInfo = getAvailabilityInfo();
  
  // Handle time selection
  const handleTimeChange = (newTime: Dayjs | null) => {
    if (newTime) {
      setSelectedTime(newTime);
    }
  };

  // Handle booking button click
  const handleBooking = () => {
    if (selectedTime) {
      // Convert dayjs object to string in format HH:MM
      const startTime = selectedTime.format('HH:mm');
      
      // Calculate end time (1 hour later)
      const endTime = selectedTime.add(1, 'hour').format('HH:mm');
      
      onTimeSelect(startTime, endTime);
    }
  };
  
  // Check if we should show time selection UI
  const showTimeSelection = availabilityStatus !== 'unavailable';
  
  // Define time slots for DigitalClock
  const getTimeSlots = () => {
    // Library hours typically 8am to 9pm
    const slots = [];
    let timePointer = dayjs().hour(8).minute(0).second(0);
    const endTime = dayjs().hour(20).minute(0).second(0);
    
    while (timePointer.isBefore(endTime)) {
      slots.push(timePointer);
      timePointer = timePointer.add(30, 'minute');
    }
    
    return slots;
  };
  
  // For limited availability, filter out some slots deterministically
  const skipTime = (time: Dayjs) => {
    if (availabilityStatus === 'available') {
      return false; // All slots available
    }
    
    if (availabilityStatus === 'unavailable') {
      return true; // No slots available
    }
    
    // For limited availability, create a pattern
    const hour = time.hour();
    const minute = time.minute();
    const dateObj = new Date(date);
    const dateHash = dateObj.getDate() + (dateObj.getMonth() * 31);
    
    // Skip morning slots with a pattern
    if (hour < 11 && (hour + dateHash) % 3 === 0) {
      return true;
    }
    
    // Skip afternoon slots with a different pattern
    if (hour >= 11 && hour < 15 && (hour + dateHash) % 4 === 0) {
      return true;
    }
    
    // Skip evening slots with another pattern
    if (hour >= 15 && (hour + minute + dateHash) % 3 === 0) {
      return true;
    }
    
    return false;
  };

  return (
    <Paper sx={{ 
      p: 3, 
      borderRadius: '12px',
      boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
      animation: 'fadeIn 0.3s ease-out',
      '@keyframes fadeIn': {
        '0%': { opacity: 0, transform: 'translateY(10px)' },
        '100%': { opacity: 1, transform: 'translateY(0)' }
      },
      maxWidth: '350px',
      mx: 'auto',
      mb: 2
    }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        <AccessTimeIcon sx={{ mr: 1, color: '#FF6B81' }} />
        <Typography variant="h6" fontWeight={600} color="#333">
          Select a Time
        </Typography>
      </Box>
      
      <Typography variant="subtitle1" sx={{ mb: 0.5, color: '#444' }}>
        {formatDate(date)}
      </Typography>
      
      <Typography 
        variant="body2" 
        sx={{ 
          mb: 2.5, 
          color: availabilityInfo.color,
          fontWeight: 500,
          display: 'flex',
          alignItems: 'center',
        }}
      >
        <Box 
          component="span" 
          sx={{ 
            display: 'inline-block', 
            width: 8, 
            height: 8, 
            borderRadius: '50%', 
            bgcolor: availabilityInfo.color,
            mr: 1
          }} 
        />
        {availabilityInfo.text}
      </Typography>
      
      {showTimeSelection ? (
        <LocalizationProvider dateAdapter={AdapterDayjs}>
          <Box sx={{ textAlign: 'center' }}>
            <DigitalClock
              value={selectedTime}
              onChange={handleTimeChange}
              skipDisabled
              shouldDisableTime={skipTime}
              timeStep={30}
              ampm
              // Keep it simple with minimal props
              sx={{
                width: '100%',
                '& .MuiDigitalClock-item': {
                  color: '#333',
                },
                '& .MuiDigitalClock-item.Mui-selected': {
                  backgroundColor: '#FF6B81',
                  color: '#fff',
                  '&:hover': {
                    backgroundColor: '#FF4757',
                  }
                }
              }}
            />
            
            <Box sx={{ mt: 3, textAlign: 'center' }}>
              {selectedTime ? (
                <>
                  <Typography variant="body2" color="#666" gutterBottom>
                    Selected time slot:
                  </Typography>
                  <Typography variant="h6" color="#333" fontWeight={600} gutterBottom>
                    {selectedTime.format('h:mm A')} - {selectedTime.add(1, 'hour').format('h:mm A')}
                  </Typography>
                  <Button
                    variant="contained"
                    onClick={handleBooking}
                    sx={{
                      mt: 2,
                      bgcolor: '#FF6B81',
                      '&:hover': {
                        bgcolor: '#FF4757',
                      }
                    }}
                  >
                    Book This Time
                  </Button>
                </>
              ) : (
                <Typography variant="body2" color="#666">
                  Please select a start time
                </Typography>
              )}
            </Box>
          </Box>
        </LocalizationProvider>
      ) : (
        <Box sx={{ 
          py: 4, 
          textAlign: 'center', 
          color: '#666',
          borderTop: '1px solid #f0f0f0',
          borderBottom: '1px solid #f0f0f0',
          my: 2
        }}>
          <Typography variant="body1" color="#FF4D4F" fontWeight={500} mb={1}>
            No available time slots for this date
          </Typography>
          <Typography variant="body2">
            Please select another date from the calendar.
          </Typography>
        </Box>
      )}
      
      <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 2, textAlign: 'center' }}>
        All bookings are for 1-hour slots
      </Typography>
    </Paper>
  );
};

export default TimeSelectorMUI;
