import React from 'react';
import { Box, Button, Typography, Paper, Grid, Chip, useTheme } from '@mui/material';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import { TimeSlot } from '../types/booking';

interface TimeSlotSelectorProps {
  timeSlots: TimeSlot[];
  onTimeSelected: (timeSlot: TimeSlot) => void;
}

const TimeSlotSelector: React.FC<TimeSlotSelectorProps> = ({ timeSlots, onTimeSelected }) => {
  const theme = useTheme();

  return (
    <Paper
      elevation={1}
      sx={{
        p: 2,
        borderRadius: 2,
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        maxWidth: '100%',
        animation: 'fadeIn 0.3s ease-out',
        '@keyframes fadeIn': {
          '0%': { opacity: 0, transform: 'translateY(10px)' },
          '100%': { opacity: 1, transform: 'translateY(0)' }
        }
      }}
    >
      <Typography 
        variant="subtitle1" 
        sx={{ mb: 1, fontWeight: 500, color: '#333', display: 'flex', alignItems: 'center' }}
      >
        <AccessTimeIcon sx={{ mr: 1, color: '#FF6B81' }} />
        Select a time slot
      </Typography>
      
      <Box sx={{ my: 2 }}>
        <Grid container spacing={1}>
          {timeSlots.map((slot, index) => {
            // Determine if the slot is available (default to true if not specified)
            const isAvailable = slot.available !== false;
            
            return (
              <Grid item xs={6} sm={4} key={index}>
                <Button
                  fullWidth
                  variant={isAvailable ? "outlined" : "outlined"}
                  disabled={!isAvailable}
                  onClick={() => isAvailable && onTimeSelected(slot)}
                  sx={{
                    borderRadius: '24px',
                    py: 1,
                    border: isAvailable ? `1px solid ${theme.palette.primary.main}` : '1px solid #e0e0e0',
                    color: isAvailable ? theme.palette.primary.main : '#bdbdbd',
                    backgroundColor: isAvailable ? 'white' : '#f5f5f5',
                    '&:hover': {
                      backgroundColor: isAvailable ? '#fff8f8' : '#f5f5f5',
                      borderColor: isAvailable ? '#FF4757' : '#e0e0e0',
                    },
                    textTransform: 'none',
                    fontWeight: 500,
                    fontSize: '0.9rem',
                    mb: 1
                  }}
                >
                  {slot.label}
                </Button>
              </Grid>
            );
          })}
        </Grid>
      </Box>
      
      <Box sx={{ mt: 1, display: 'flex' }}>
        <Chip
          label="Available"
          variant="outlined"
          size="small"
          sx={{ 
            borderColor: theme.palette.primary.main, 
            color: theme.palette.primary.main,
            mr: 1
          }}
        />
        <Chip
          label="Unavailable"
          variant="outlined"
          size="small"
          sx={{ 
            borderColor: '#e0e0e0', 
            color: '#bdbdbd',
            backgroundColor: '#f5f5f5'
          }}
        />
      </Box>
    </Paper>
  );
};

export default TimeSlotSelector;
