import React, { useState, useRef, useEffect } from 'react';
import { 
  Box, 
  Fab, 
  Paper, 
  Typography, 
  TextField,
  IconButton,
  Chip,
  CircularProgress
} from '@mui/material';
import socketService from '../services/socketService';
import SendIcon from '@mui/icons-material/Send';
import CloseIcon from '@mui/icons-material/Close';
import MinimizeIcon from '@mui/icons-material/Minimize';
import SchoolIcon from '@mui/icons-material/School';
import WorkIcon from '@mui/icons-material/Work';
import PersonIcon from '@mui/icons-material/Person';
import DateSelector from './DateSelector';
import TimeSlotSelector from './TimeSlotSelector';
import { BookingResponse, TimeSlot } from '../types/booking';

import { useUser, UserType } from '../contexts/UserContext';

// URL for Cougar SVG logo
const cougarLogoUrl = "https://dxbhsrqyrr690.cloudfront.net/sidearm.nextgen.sites/clarku.sidearmsports.com/images/responsive_2023/CU%20Cougar%20on%20White.svg";

interface Message {
  id: number;
  text: string;
  sender: 'user' | 'ai';
  timestamp: Date;
  bookingResponse?: BookingResponse; // Optional booking-related data
}

const ChatWidget: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isSelectingUserType, setIsSelectingUserType] = useState(true);
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [isAnimating, setIsAnimating] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [connectionError, setConnectionError] = useState<string | null>(null);
  const [showDateSelector, setShowDateSelector] = useState<boolean>(false);
  const [showTimeSelector, setShowTimeSelector] = useState<boolean>(false);
  const [timeSlots, setTimeSlots] = useState<TimeSlot[]>([]);
  const [lastBookingMessage, setLastBookingMessage] = useState<Message | null>(null);
  const chatBottomRef = useRef<HTMLDivElement>(null);
  const { user, setUserType } = useUser();

  // Clear chat history when page is reloaded
  useEffect(() => {
    localStorage.removeItem('sessionId');
    console.log('Cleared chat session due to page reload');
  }, []);

  // Scroll to bottom of chat when messages update
  useEffect(() => {
    if (chatBottomRef.current) {
      chatBottomRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  const toggleChat = () => {
    setIsAnimating(true);
    setIsOpen(!isOpen);
    
    // Reset animation state after animation completes
    setTimeout(() => {
      setIsAnimating(false);
    }, 300);
  };

  // Connect to socket when user selects type
  useEffect(() => {
    if (user?.type && !isSelectingUserType) {
      // Show connecting status
      setConnectionError(null);
      setIsLoading(true);
      
      let isMounted = true;
      
      // Attempt to connect with retry logic
      const connectWithRetry = (retries = 3) => {
        console.log('Attempting to connect to socket server, retries left:', retries);
        
        socketService.connect(user.type!)
          .then(() => {
            if (!isMounted) return;
            
            console.log('Socket connection successful');
            setConnectionError(null);
            
            // Try to load chat history
            return socketService.getChatHistory();
          })
          .then(history => {
            if (!isMounted || !history) return;
            
            if (history.length > 0) {
              console.log('Setting chat history:', history);
              setMessages(history.map(msg => ({
                id: typeof msg.id === 'string' ? parseInt(msg.id) : (msg.id as number || Date.now()),
                text: msg.text,
                sender: msg.sender,
                timestamp: msg.timestamp instanceof Date ? msg.timestamp : new Date(msg.timestamp),
                bookingResponse: (msg as any).bookingResponse
              })));
            }
            setIsLoading(false);
          })
          .catch(error => {
            console.error('Connection error:', error);
            if (!isMounted) return;
            
            if (retries > 0) {
              // Retry connection after delay
              setTimeout(() => connectWithRetry(retries - 1), 2000);
            } else {
              setConnectionError('Could not connect to AI service. Please refresh and try again.');
              setIsLoading(false);
            }
          });
      };
      
      // Set up message listener
      const cleanupListener = socketService.onMessage((message) => {
        if (!isMounted) return;
        
        console.log('Processing received message:', message);
        
        // Create the new message
        const newMessage = {
          id: typeof message.id === 'string' ? parseInt(message.id) : (message.id || Date.now()),
          text: message.text,
          sender: message.sender,
          timestamp: message.timestamp instanceof Date ? message.timestamp : new Date(message.timestamp),
          bookingResponse: (message as any).bookingResponse
        };
        
        setMessages(prev => [...prev, newMessage]);
        setIsLoading(false);
        
        // Handle booking UI components if needed
        if ((message as any).bookingResponse) {
          setLastBookingMessage(newMessage);
          const bookingResponse = (message as any).bookingResponse;
          
          // Show date selector if requested
          if (bookingResponse.showDateSelector) {
            setShowDateSelector(true);
            setShowTimeSelector(false);
          }
          
          // Show time selector if requested
          if (bookingResponse.showTimeSelector) {
            setShowTimeSelector(true);
            setShowDateSelector(false);
            
            // Set time slots
            if (bookingResponse.timeSlots) {
              setTimeSlots(bookingResponse.timeSlots);
            }
          }
          
          // Hide selectors when not needed
          if (!bookingResponse.showDateSelector && !bookingResponse.showTimeSelector) {
            setShowDateSelector(false);
            setShowTimeSelector(false);
          }
        }
      });
      
      // Start connection process
      connectWithRetry();
      
      // Cleanup function
      return () => {
        isMounted = false;
        cleanupListener();
        socketService.disconnect();
      };
    }
  }, [user?.type, isSelectingUserType]);

  const handleUserSelect = (type: UserType) => {
    setUserType(type);
    setIsSelectingUserType(false);
    setConnectionError(null);
    
    // Add welcome message - this will be shown immediately while we connect to socket
    const welcomeMessage = getWelcomeMessage(type);
    setMessages([
      { 
        id: Date.now(), 
        text: welcomeMessage, 
        sender: 'ai', 
        timestamp: new Date() 
      }
    ]);
  };

  const getWelcomeMessage = (type: UserType): string => {
    switch (type) {
      case 'student':
        return 'Welcome to Clark AI! I can help you with course information, campus resources, ClarkYou credentials, OneCard queries, appointment scheduling, study room booking, and more. What can I assist you with today?';
      case 'faculty':
        return 'Welcome to Clark AI! I can help you with campus resources, course schedules, study room booking, and other faculty services. How can I assist you today?';
      case 'other':
        return 'Welcome to Clark AI! I can provide information about commuter options, places near Clark, study room booking, and general university information. What would you like to know?';
      default:
        return 'Welcome to Clark AI! How can I help you today?';
    }
  };

  const handleSendMessage = async (messageText?: string, isComponentGenerated = false) => {
    const textToSend = messageText || input;
    
    if (!textToSend.trim() && !isComponentGenerated) return;
    
    // Check connection status first
    if (!socketService.isConnected()) {
      setConnectionError('Connection to server lost. Attempting to reconnect...');
      
      try {
        // Try to reconnect
        await socketService.connect(user?.type || 'student');
        // Clear error if successful
        setConnectionError(null);
      } catch (error) {
        console.error('Reconnection failed:', error);
        setConnectionError('Could not reconnect to AI service. Please refresh the page.');
        return;
      }
    }

    // Add user message to the UI immediately
    const userMessage: Message = {
      id: Date.now(),
      text: textToSend,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    
    if (!isComponentGenerated) {
      setInput('');
    }
    
    setIsLoading(true);
    
    try {
      // Send message to AI backend
      await socketService.sendMessage(textToSend);
      
      // The response will be handled by the socket listener in useEffect
    } catch (error) {
      console.error('Error sending message:', error);
      setIsLoading(false);
      
      // Show error message with more detail
      setMessages(prev => [...prev, {
        id: Date.now(),
        text: error instanceof Error ? 
          `Sorry, I encountered an error processing your request. ${error.message}` : 
          "Sorry, I couldn't process your message. Please try again later.",
        sender: 'ai',
        timestamp: new Date()
      }]);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleChipClick = (text: string) => {
    setInput(text);
  };

  const resetChat = () => {
    setIsSelectingUserType(true);
    setMessages([]);
    setShowDateSelector(false);
    setShowTimeSelector(false);
  };

  return (
    <>
      {/* Chat Button */}
      {!isOpen && (
        <Fab
          color="primary"
          aria-label="chat"
          sx={{
            position: 'fixed',
            bottom: 24,
            right: 24,
            bgcolor: '#FF6B81',
            zIndex: 9999,
            '&:hover': {
              bgcolor: '#FF4757',
            },
            padding: '8px',
          }}
          onClick={toggleChat}
        >
          <Box 
            component="img" 
            src={cougarLogoUrl}
            alt="Clark University Cougar"
            sx={{ 
              width: '30px',
              height: '30px',
              objectFit: 'contain',
            }}
          />
        </Fab>
      )}

      {/* Chat Window */}
      {isOpen && (
        <Paper
          elevation={4}
          sx={{
            position: 'fixed',
            bottom: 24,
            right: 24,
            width: 400,
            height: 520,
            borderRadius: 2,
            overflow: 'hidden',
            display: 'flex',
            flexDirection: 'column',
            animation: isAnimating ? 'fadeIn 0.3s ease-in-out' : 'none',
            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.15)',
            zIndex: 9999,
            '@keyframes fadeIn': {
              '0%': { opacity: 0, transform: 'translateY(20px) scale(0.9)' },
              '100%': { opacity: 1, transform: 'translateY(0) scale(1)' }
            },
          }}
        >
          {/* Chat Header */}
          <Box
            sx={{
              bgcolor: '#FF6B81',
              color: 'white',
              p: 1.5,
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
              position: 'relative'
            }}
          >
            <Box sx={{ display: 'flex', alignItems: 'center', ml: 1 }}>
              <img 
                src="https://www.clarku.edu/wp-content/themes/clarku/assets/img/main-logo.svg" 
                alt="Clark University Logo" 
                style={{ 
                  height: '28px', 
                  marginRight: '8px'
                }} 
              />
              <Typography 
                variant="h6" 
                component="div" 
                sx={{ 
                  fontFamily: '"Merriweather", serif',
                  fontWeight: 700,
                  fontSize: '1rem',
                  letterSpacing: '0.5px'
                }}
              >
                AI
              </Typography>
            </Box>
            <Box sx={{ flexGrow: 1 }} />
            
            <Box>
              <IconButton
                color="inherit"
                size="small"
                onClick={() => setIsOpen(false)}
                sx={{ mr: 0.5 }}
              >
                <MinimizeIcon />
              </IconButton>
              <IconButton
                color="inherit"
                size="small"
                onClick={() => {
                  setIsOpen(false);
                  resetChat();
                }}
              >
                <CloseIcon />
              </IconButton>
            </Box>
          </Box>

          {/* User Type Selection */}
          {isSelectingUserType ? (
            <Box 
              sx={{ 
                p: 3, 
                overflow: 'auto', 
                flexGrow: 1,
                background: 'linear-gradient(to bottom, #f8f8f8, #ffffff)',
                position: 'relative'
              }}
            >
              <Typography 
                variant="h5" 
                component="div" 
                gutterBottom
                sx={{ 
                  fontFamily: '"Merriweather", serif',
                  fontWeight: 700,
                  color: '#2a2a2a',
                  textAlign: 'center',
                  mb: 3
                }}
              >
                Hello I am ClarkGuide
              </Typography>
              
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1.5, mt: 2 }}>
                <Paper
                  elevation={2}
                  sx={{
                    p: 1.5,
                    cursor: 'pointer',
                    borderRadius: '12px'
                  }}
                  onClick={() => handleUserSelect('student')}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <SchoolIcon sx={{ mr: 2, color: '#FF6B81' }} />
                    <Box>
                      <Typography variant="subtitle1">Student</Typography>
                      <Typography variant="body2" color="text.secondary">
                        Access course info, campus resources, ClarkYou help, and more
                      </Typography>
                    </Box>
                  </Box>
                </Paper>
                
                <Paper
                  elevation={2}
                  sx={{
                    p: 1.5,
                    cursor: 'pointer',
                    borderRadius: '12px'
                  }}
                  onClick={() => handleUserSelect('faculty')}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <WorkIcon sx={{ mr: 2, color: '#FF6B81' }} />
                    <Box>
                      <Typography variant="subtitle1">Faculty</Typography>
                      <Typography variant="body2" color="text.secondary">
                        Campus resources, course schedules, and general support
                      </Typography>
                    </Box>
                  </Box>
                </Paper>
                
                <Paper
                  elevation={2}
                  sx={{
                    p: 1.5,
                    cursor: 'pointer',
                    borderRadius: '12px'
                  }}
                  onClick={() => handleUserSelect('other')}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <PersonIcon sx={{ mr: 2, color: '#FF6B81' }} />
                    <Box>
                      <Typography variant="subtitle1">Other</Typography>
                      <Typography variant="body2" color="text.secondary">
                        Transportation, places near Clark, and general information
                      </Typography>
                    </Box>
                  </Box>
                </Paper>
              </Box>
              
              <Typography 
                variant="caption" 
                sx={{ 
                  display: 'block', 
                  textAlign: 'center', 
                  mt: 5,
                  color: 'rgba(0,0,0,0.5)',
                  fontStyle: 'italic'
                }}
              >
                 2025 Clark University Hackathon Project
              </Typography>
            </Box>
          ) : (
            <>
              {/* Chat Messages and Interactive Components */}
              <Box sx={{ p: 2, overflow: 'auto', flexGrow: 1 }}>
                {connectionError && (
                  <Box 
                    sx={{ 
                      p: 2, 
                      mb: 2, 
                      bgcolor: '#fff0f0', 
                      borderRadius: 2,
                      border: '1px solid #ffcdd2' 
                    }}
                  >
                    <Typography variant="body2" color="error">
                      {connectionError}
                    </Typography>
                  </Box>
                )}
                
                {/* Messages */}
                {messages.map((message) => (
                  <Box
                    key={message.id}
                    sx={{
                      display: 'flex',
                      justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start',
                      mb: 2
                    }}
                  >
                    <Paper
                      elevation={1}
                      sx={{
                        p: 1.5,
                        maxWidth: '80%',
                        borderRadius: message.sender === 'user' ? '18px 18px 4px 18px' : '18px 18px 18px 4px',
                        bgcolor: message.sender === 'user' ? '#FF6B81' : '#f5f5f5',
                        color: message.sender === 'user' ? 'white' : 'inherit'
                      }}
                    >
                      <Typography variant="body2">
                        {message.text}
                      </Typography>
                      <Typography variant="caption" color={message.sender === 'user' ? 'rgba(255,255,255,0.7)' : 'text.secondary'} sx={{ display: 'block', mt: 0.5 }}>
                        {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </Typography>
                    </Paper>
                  </Box>
                ))}
                
                {/* Date Selector (for booking) */}
                {showDateSelector && (
                  <Box sx={{ my: 2, maxWidth: '100%' }}>
                    <DateSelector onDateSelected={(date) => {
                      setShowDateSelector(false);
                      setInput('');
                      const dateStr = date.toLocaleDateString('en-US', {
                        month: '2-digit', 
                        day: '2-digit', 
                        year: 'numeric'
                      });
                      handleSendMessage(dateStr, true);
                    }} />
                  </Box>
                )}
                
                {/* Time Slot Selector (for booking) */}
                {showTimeSelector && timeSlots.length > 0 && (
                  <Box sx={{ my: 2, maxWidth: '100%' }}>
                    <TimeSlotSelector 
                      timeSlots={timeSlots}
                      onTimeSelected={(timeSlot) => {
                        setShowTimeSelector(false);
                        setInput('');
                        handleSendMessage(timeSlot.label, true);
                      }}
                    />
                  </Box>
                )}
                
                <div ref={chatBottomRef} />
                
                {/* Loading indicator */}
                {isLoading && (
                  <Box
                    sx={{
                      display: 'flex',
                      justifyContent: 'flex-start',
                      alignItems: 'center',
                      mb: 2,
                      pl: 2
                    }}
                  >
                    <Paper
                      elevation={1}
                      sx={{
                        p: 2,
                        maxWidth: '80%',
                        borderRadius: '18px 18px 18px 4px',
                        bgcolor: '#f5f5f5'
                      }}
                    >
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <CircularProgress size={16} sx={{ color: '#FF6B81' }} />
                        <Typography 
                          variant="body2"
                          sx={{ 
                            color: '#666'
                          }}
                        >
                          Thinking...
                        </Typography>
                      </Box>
                    </Paper>
                  </Box>
                )}
              </Box>

              {/* Input Area */}
              <Box sx={{ p: 2, borderTop: 1, borderColor: 'divider' }}>
                <Box sx={{ display: 'flex', mb: 1 }}>
                  <TextField
                    fullWidth
                    size="small"
                    placeholder="Type your message..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={handleKeyPress}
                    variant="outlined"
                    sx={{ mr: 1 }}
                  />
                  <IconButton
                    color="primary"
                    onClick={() => handleSendMessage()}
                    disabled={!input.trim()}
                    sx={{ bgcolor: '#FF6B81', color: 'white', '&:hover': { bgcolor: '#FF4757' } }}
                  >
                    <SendIcon />
                  </IconButton>
                </Box>
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                  <Chip
                    label="Library hours"
                    size="small"
                    variant="outlined"
                    onClick={() => handleChipClick('What are the library hours?')}
                    clickable
                  />
                  <Chip
                    label="Book a study room"
                    size="small"
                    variant="outlined"
                    onClick={() => handleChipClick('I want to book a study room')}
                    clickable
                  />
                  <Chip
                    label="Courses"
                    size="small"
                    variant="outlined"
                    onClick={() => handleChipClick('What is my course schedule?')}
                    clickable
                  />
                </Box>
              </Box>
            </>
          )}
        </Paper>
      )}
    </>
  );
};

export default ChatWidget;
