import React from 'react';
import { Box, Paper, Typography } from '@mui/material';
import { LocalizationProvider, DateCalendar } from '@mui/x-date-pickers';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import EventIcon from '@mui/icons-material/Event';
import dayjs, { Dayjs } from 'dayjs';

interface CalendarSelectorProps {
  onDateSelect: (date: Date) => void;
  minDate?: Date;
  maxDate?: Date;
}

// Define availability status types
type AvailabilityStatus = 'unavailable' | 'limited' | 'available';

const CalendarSelectorMUI: React.FC<CalendarSelectorProps> = ({ 
  onDateSelect,
  minDate = new Date(), // Default to today
  maxDate // Optional max date
}) => {
  // Set max date to 30 days from now if not provided
  const defaultMaxDate = new Date();
  defaultMaxDate.setDate(defaultMaxDate.getDate() + 30);
  const effectiveMaxDate = maxDate || defaultMaxDate;
  
  // Ensure minDate is not in the past (today or future)
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  const effectiveMinDate = minDate < today ? today : minDate;
  
  // Check if a date is within the coming week
  const isInComingWeek = (date: Date): boolean => {
    const nextWeekStart = new Date(today);
    const nextWeekEnd = new Date(today);
    nextWeekEnd.setDate(today.getDate() + 7);
    
    return date >= nextWeekStart && date < nextWeekEnd;
  };
  
  // Determine the availability status of a date
  const getAvailabilityStatus = (date: Date | null): AvailabilityStatus => {
    if (!date || !isDateSelectable(date)) return 'unavailable';
    
    // If in the coming week, apply specific day-based rules
    if (isInComingWeek(date)) {
      const dayOfWeek = date.getDay();
      
      // Monday (1) or Tuesday (2) are unavailable
      if (dayOfWeek === 1 || dayOfWeek === 2) {
        return 'unavailable';
      }
      
      // Thursday (4) or Saturday (6) have limited availability
      if (dayOfWeek === 4 || dayOfWeek === 6) {
        return 'limited';
      }
      
      // Wednesday (3) and Friday (5) and Sunday (0) are available
      return 'available';
    }
    
    // All dates beyond the coming week are available
    return 'available';
  };
  
  // Check if a date is selectable
  const isDateSelectable = (date: Date | null): boolean => {
    if (!date) return false;
    
    // Base selectability on min/max date constraints
    const withinDateRange = date >= effectiveMinDate && date <= effectiveMaxDate;
    
    if (!withinDateRange) return false;
    
    // Additional check for availability - red dates are not selectable
    if (isInComingWeek(date)) {
      const dayOfWeek = date.getDay();
      if (dayOfWeek === 1 || dayOfWeek === 2) { // Monday or Tuesday
        return false;
      }
    }
    
    return true;
  };
  
  // Get color for date based on availability
  const getDateColor = (date: Date): string => {
    const status = getAvailabilityStatus(date);
    
    switch (status) {
      case 'unavailable':
        return '#FF4D4F'; // Red
      case 'limited':
        return '#FFA940'; // Orange/Amber
      case 'available':
        return '#52C41A'; // Green
      default:
        return 'inherit';
    }
  };
  
  const handleDateSelect = (value: Dayjs | null) => {
    if (value) {
      const date = value.toDate();
      if (isDateSelectable(date)) {
        onDateSelect(date);
      }
    }
  };
  
  return (
    <Paper sx={{ 
      p: 3, // Increased padding
      borderRadius: '12px',
      boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
      animation: 'fadeIn 0.3s ease-out',
      '@keyframes fadeIn': {
        '0%': { opacity: 0, transform: 'translateY(10px)' },
        '100%': { opacity: 1, transform: 'translateY(0)' }
      },
      maxWidth: '350px',
      mx: 'auto',
      mb: 2
    }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        <EventIcon sx={{ mr: 1, color: '#FF6B81' }} />
        <Typography variant="h6" fontWeight={600} color="#333">
          Select a Date
        </Typography>
      </Box>
      
      <LocalizationProvider dateAdapter={AdapterDayjs}>
        <DateCalendar 
          value={null}
          onChange={handleDateSelect}
          minDate={dayjs(effectiveMinDate)}
          maxDate={dayjs(effectiveMaxDate)}
          disablePast
          // Format day names to 3-letter abbreviations
          dayOfWeekFormatter={(day) => day.toString().substring(0, 3)}
          slots={{
            day: (props) => {
              // Get the date from props
              const date = props.day?.toDate();
              
              // Skip if undefined
              if (!date) {
                return <></>;
              }
              
              // Determine if the date is selectable
              const selectable = isDateSelectable(date);
              
              // Get the color based on availability
              let textColor = getDateColor(date);
              
              // Adjust styling for today
              const isToday = dayjs(date).isSame(dayjs(), 'day');
              
              return (
                <Box 
                  onClick={() => selectable && handleDateSelect(dayjs(date))}
                  sx={{
                    width: 36,
                    height: 36,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: selectable ? 'pointer' : 'not-allowed',
                    opacity: selectable ? 1 : 0.7,
                    borderRadius: '50%',
                    bgcolor: isToday ? 'rgba(255, 107, 129, 0.1)' : 'transparent',
                    border: isToday ? '1px solid #FF6B81' : 'none',
                    '&:hover': selectable ? {
                      bgcolor: 'rgba(255, 107, 129, 0.2)',
                    } : {}
                  }}
                >
                  <Typography
                    color={textColor}
                    fontWeight={isToday ? 600 : 500}
                    variant="body2"
                  >
                    {date.getDate()}
                  </Typography>
                </Box>
              );
            }
          }}
        />
      </LocalizationProvider>
      
      {/* Legend */}
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        px: 2, 
        py: 1,
        mb: 2,
        borderTop: '1px solid #f0f0f0' 
      }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: '#FF4D4F', mr: 1 }} />
          <Typography variant="caption" color="#666">Unavailable</Typography>
        </Box>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: '#FFA940', mr: 1 }} />
          <Typography variant="caption" color="#666">Limited</Typography>
        </Box>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: '#52C41A', mr: 1 }} />
          <Typography variant="caption" color="#666">Available</Typography>
        </Box>
      </Box>
      
      <Typography variant="caption" color="text.secondary" sx={{ display: 'block', textAlign: 'center' }}>
        You can book a room up to 30 days in advance
      </Typography>
    </Paper>
  );
};

export default CalendarSelectorMUI;
